   document.addEventListener("DOMContentLoaded", function () {
       const waitlistButton = document.querySelector(".general-cta .btn-rounded-primary"); 
       const emailSignupSection = document.querySelector(".ampliance_layout.email-signup-form");
       if (waitlistButton && emailSignupSection) {
