               gtag("event", "scroll_to_waitlist", {
                   event_category: "engagement",
                   event_label: "Waitlist Scroll",
                   value: 1
               });
           });
       }
   });
   document.addEventListener("DOMContentLoaded", function () {
       const targetDateInput = document.querySelector(".countdown-container input[type='hidden']");
       const targetDate = new Date(targetDateInput.value).getTime();
       const daysElem = document.querySelector(".countdown-item .days");
       const hoursElem = document.querySelector(".countdown-item .hours");
       const minutesElem = document.querySelector(".countdown-item .minutes");
       const secondsElem = document.querySelector(".countdown-item .seconds");
       function updateCountdown() {
           const now = new Date().getTime();
           const timeLeft = targetDate - now;
           if (timeLeft <= 0) {
               clearInterval(countdownInterval);
               daysElem.textContent = "00";
               hoursElem.textContent = "00";
               minutesElem.textContent = "00";
               secondsElem.textContent = "00";
               return;
           }
           const days = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
           const hours = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
           const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
           const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);
           daysElem.textContent = days.toString().padStart(2, "0");
           hoursElem.textContent = hours.toString().padStart(2, "0");
           minutesElem.textContent = minutes.toString().padStart(2, "0");
           secondsElem.textContent = seconds.toString().padStart(2, "0");
       }
       // Run every second
       const countdownInterval = setInterval(updateCountdown, 1000);
       // Initial call to avoid 1-second delay
       updateCountdown();
   });
   </script>
        
    

 
	
